// src/App.js

import React, { useRef, useEffect } from 'react';
import mapboxgl from 'mapbox-gl';
import geojsonData from './data/UNI_shpfile_displayTrial.geojson';
import './App.css';

// Set your Mapbox access token here
mapboxgl.accessToken = 'YOUR_MAPBOX_ACCESS_TOKEN';

function App() {
  const mapContainer = useRef(null);
  const map = useRef(null);
  const lng = -74.0060; // Set your desired longitude
  const lat = 40.7128;  // Set your desired latitude
  const zoom = 9;       // Set your desired zoom level

  useEffect(() => {
    if (map.current) return; // Initialize map only once

    map.current = new mapboxgl.Map({
      container: mapContainer.current,
      style: 'mapbox://styles/mapbox/streets-v11',
      center: [lng, lat],
      zoom: zoom,
    });

    // Add navigation controls (optional)
    map.current.addControl(new mapboxgl.NavigationControl());

    // Add GeoJSON data as a source
    map.current.on('load', () => {
      map.current.addSource('uniData', {
        type: 'geojson',
        data: geojsonData,
      });

      // Add a layer to display the GeoJSON data
      map.current.addLayer({
        id: 'uniLayer',
        type: 'fill', // Change to 'circle', 'line', etc., depending on your data
        source: 'uniData',
        paint: {
          'fill-color': '#888888',
          'fill-opacity': 0.5,
        },
      });
    });
  }, []);

  return (
    <div>
      <div ref={mapContainer} className="map-container" />
    </div>
  );
}

export default App;
